import{_ as a,o as e,c as n,a as t}from"./app.7c6fd316.js";const D=JSON.parse('{"title":"浅析 script 标签下的 async 和 defer","description":"","frontmatter":{},"headers":[],"relativePath":"01.JavaScript/浅析script标签下的async和defer.md","lastUpdated":1763393448000}'),l={name:"01.JavaScript/浅析script标签下的async和defer.md"};function p(o,s,c,r,i,d){return e(),n("div",{"data-pagefind-body":!0},[...s[0]||(s[0]=[t(`<h1 id="浅析-script-标签下的-async-和-defer" tabindex="-1">浅析 script 标签下的 async 和 defer <a class="header-anchor" href="#浅析-script-标签下的-async-和-defer" aria-hidden="true">#</a></h1><p>前言：</p><p>在 HTML 中，<code>&lt;script&gt;</code>标签用于引入外部的 JavaScript 文件。它可以通过<code>async</code>和<code>defer</code>属性来控制脚本的加载方式。本文将介绍这两个属性的区别以及如何使用它们。</p><ol><li>async 属性</li></ol><p><code>async</code>属性用于异步加载脚本，即在不影响页面其他内容的情况下加载脚本。当脚本加载完成后，会立即执行脚本。</p><p>比较特别，因为在下载后会立刻执行，且不保证执行顺序，一般常见的应用是设定在完全独立的小小模块中，例如背景 Logo、页面广告等，在避免造成使用者体验变差的同时，尽量早的产生效果</p><div class="language-html line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">html</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">script</span><span style="color:#89DDFF;"> </span><span style="color:#C792EA;">async</span><span style="color:#89DDFF;"> </span><span style="color:#C792EA;">src</span><span style="color:#89DDFF;">=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">script.js</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">&gt;&lt;/</span><span style="color:#F07178;">script</span><span style="color:#89DDFF;">&gt;</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><ol start="2"><li>defer 属性</li></ol><p><code>defer</code>属性用于延迟加载脚本，即在页面加载完成后再加载脚本。当脚本加载完成后，会在 DOMContentLoaded 事件之前执行脚本。</p><div class="language-html line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">html</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">script</span><span style="color:#89DDFF;"> </span><span style="color:#C792EA;">defer</span><span style="color:#89DDFF;"> </span><span style="color:#C792EA;">src</span><span style="color:#89DDFF;">=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">script.js</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">&gt;&lt;/</span><span style="color:#F07178;">script</span><span style="color:#89DDFF;">&gt;</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><ol start="3"><li>区别</li></ol><ul><li><code>async</code>属性：脚本加载完成后立即执行，不阻塞页面的渲染。</li><li><code>defer</code>属性：脚本加载完成后在 DOMContentLoaded 事件之前执行，阻塞页面的渲染。</li></ul>`,12)])])}const F=a(l,[["render",p]]);export{D as __pageData,F as default};
