import{_ as s,o as a,c as n,a as l}from"./app.7c6fd316.js";const m=JSON.parse('{"title":"node_modules 删除难题","description":"","frontmatter":{},"headers":[{"level":2,"title":"系统原生命令的快速删除方案","slug":"系统原生命令的快速删除方案","link":"#系统原生命令的快速删除方案","children":[]}],"relativePath":"02.NodeJs/node_modules删除难题.md","lastUpdated":1763393448000}'),o={name:"02.NodeJs/node_modules删除难题.md"};function d(p,e,r,t,i,c){return a(),n("div",{"data-pagefind-body":!0},[...e[0]||(e[0]=[l(`<h1 id="node-modules-删除难题" tabindex="-1">node_modules 删除难题 <a class="header-anchor" href="#node-modules-删除难题" aria-hidden="true">#</a></h1><p>在前端开发的世界里，node_modules 文件夹几乎是每个开发者心中的“痛”。每次项目依赖更新或者环境清理时，手动删除这个庞大的文件夹总是让人头疼不已。尤其是当项目依赖复杂时，node_modules 文件夹动辄几百 MB 甚至几个 G，手动删除时进度条缓慢移动，简直能把人逼疯！今天，就让我们一起探索如何高效删除 node_modules，告别“龟速”删除的噩梦。</p><p>rimraf 是 node 的一个包，可以用来删除文件夹，但是，它不能删除文件，因此，我们需要使用 fs 模块来删除文件。</p><p>首先，我们需要安装 rimraf 和 fs 模块：</p><div class="language- line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#babed8;">npm install rimraf -g</span></span>
<span class="line"><span style="color:#babed8;"></span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div><p>一键删除：进入项目根目录，执行：</p><div class="language- line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#babed8;">rimraf node_modules</span></span>
<span class="line"><span style="color:#babed8;"></span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div><h2 id="系统原生命令的快速删除方案" tabindex="-1">系统原生命令的快速删除方案 <a class="header-anchor" href="#系统原生命令的快速删除方案" aria-hidden="true">#</a></h2><p>系统原生命令的快速删除方案</p><ul><li>windows：</li></ul><p>CMD 命令</p><blockquote><p>rmdir /s /q node_modules</p></blockquote><p>/s 表示递归删除，/q 表示静默执行，无需弹窗确认。</p><p>PowerShell（更快）：</p><blockquote><p>Remove-Item -Recurse -Force node_modules</p></blockquote><ul><li>mac/linux：</li></ul><blockquote><p>rm -rf node_modules</p></blockquote>`,17)])])}const b=s(o,[["render",d]]);export{m as __pageData,b as default};
